import './App.css';
import DeadMansContract from './utils/DeadMansContract.abi.json';
import { ethers, ContractFactory } from "ethers";
import { useState } from 'react';

import { Layout, Collapse, Row, Col, Menu, Button } from 'antd';
import "antd/dist/antd.css";
import { Typography, Divider } from 'antd';

const { Title } = Typography;
const { Header, Content, Footer } = Layout;
const { Panel } = Collapse;

function App() {
  const [currentAccount, setCurrentAccount] = useState("");

  const connectWallet = async () => {
    try {
      const { ethereum } = window;

      if (!ethereum) {
        alert("This app only works with MetaMask for now!");
        return;
      }

      const accounts = await ethereum.request({ method: "eth_requestAccounts" });
      console.log("Connected", accounts[0]);
      setCurrentAccount(accounts[0]);
    } catch (error) {
      console.log(error)
    }
  }

  const createContract = async () => {
    try {
      const { ethereum } = window;
      if (ethereum) {
        const provider = new ethers.providers.Web3Provider(ethereum)
        const signer = provider.getSigner();
        const decryptionKey = document.getElementById("decryptionKey").value;
        const cadence = document.getElementById("cadence").value;

        const factory = new ContractFactory(DeadMansContract.abi, DeadMansContract.bytecode, signer);

        const contract = await factory.deploy(decryptionKey, cadence);

        await contract.deployTransaction.wait();

        // console.log(contract.address);
      } else {
        console.log("Ethereum object doesn't exist!")
      }
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <Layout>
      <Header style={{ position: "fixed", zIndex: 1, width: "100%" }}>
        <Menu theme="dark" mode="horizontal" >
          <Menu.Item key="1">nav 1</Menu.Item>
          <Menu.Item key="2">nav 2</Menu.Item>
          <Menu.Item key="3">nav 3</Menu.Item>
        </Menu>
      </Header>
      <Content style={{ marginTop: "8rem" }}>
        <Row justify="center">
          <Col span={18}>
            <Title>Dead Man's Switch</Title>
            {!currentAccount ? (
              <button className="waveButton" onClick={connectWallet}>
                Connect Wallet
              </button>
            ) : <h1>Currently connected as: {currentAccount}</h1>
            }

            <h2>What is a Dead Man's Switch?</h2>
            <p>Lorem Ipsum. Some links. blah blah</p>

            <Divider plain>Check on deployed Dead Man's Switch</Divider>
            <div>
              <input placeholder="Address of contract"></input>
              <Button onClick="getContract">Get Contract</Button>
            </div>
            {/* If user already has a DMS with this contract, show UI to interact with contract */}

            <Divider plain>Deploy a new Dead Man's Switch</Divider>
            <div>
              <input id="decryptionKey" placeholder="Decryption Key"></input>
              <input id="cadence" placeholder="When to check in hours" max="168"></input>
              <Button onClick={createContract}>Create Dead Man's Switch</Button>
            </div>

            <div style={{ marginTop: "8rem" }}>
              <Collapse >
                <Panel header="Why is the blockchain perfect for this?"><p>Lorem Ipsum</p></Panel>
                <Panel header="How it works"><p>Lorem Ipsum</p></Panel>
                <Panel header="Does it cost anything?"><p>Lorem Ipsum</p></Panel>
              </Collapse>
            </div>
          </Col>
        </Row>

      </Content>

      <Footer>Disclaimer</Footer>
    </Layout>
  );
}

export default App;
