import './App.css';
import DeadMansContract from './utils/DeadMansContract.abi.json';
import { ethers, ContractFactory } from "ethers";
import { useState } from 'react';

import { Layout, Row, Col } from 'antd';
import "antd/dist/antd.css";
const { Header, Content, Footer } = Layout;


function App() {
  const [currentAccount, setCurrentAccount] = useState("");

  const connectWallet = async () => {
    try {
      const { ethereum } = window;

      if (!ethereum) {
        alert("This app only works with MetaMask for now!");
        return;
      }

      const accounts = await ethereum.request({ method: "eth_requestAccounts" });
      console.log("Connected", accounts[0]);
      setCurrentAccount(accounts[0]);
    } catch (error) {
      console.log(error)
    }
  }

  const createContract = async () => {
    try {
      const { ethereum } = window;
      if (ethereum) {
        const provider = new ethers.providers.Web3Provider(ethereum)
        const signer = provider.getSigner();
        const decryptionKey = document.getElementById("decryptionKey").value;
        const cadence = document.getElementById("cadence").value;

        const factory = new ContractFactory(DeadMansContract.abi, DeadMansContract.bytecode, signer);

        const contract = await factory.deploy(decryptionKey, cadence);

        await contract.deployTransaction.wait();

        // console.log(contract.address);
      } else {
        console.log("Ethereum object doesn't exist!")
      }
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <Layout>
      <Content>
        <Row>
          <Col>
        {!currentAccount ? (
          <button className="waveButton" onClick={connectWallet}>
            Connect Wallet
          </button>
        ) : <h1>Currently connected as: {currentAccount}</h1>
        }

        <h1>What is a Dead Man's Switch?</h1>
        <h1>Why is the blockchain perfect for this?</h1>
        <h1>How this works</h1>

        <div>
          <input id="decryptionKey" placeholder="Decryption Key"></input>
          <input id="cadence" placeholder="When to check in hours" max="168"></input>
          <button onClick={createContract}>Create Dead Man's Switch</button>
        </div>
</Col>

        {/* User checks in on their DMS */}
        {/* <form>
        <input placeholder="Address of contract"></input>
        <button onClick="getContract">{}</button>
      </form> */}
      </Row>
      </Content>
      <Footer></Footer>
    </Layout>
  );
}

export default App;
