import { Modal, Button, Space, Input, Spin } from 'antd';
import React from 'react';
import DeadMansContract from '../utils/DeadMansContract.abi.json';
import { ethers } from "ethers";
import { LoadingOutlined } from '@ant-design/icons';

const antIcon = <LoadingOutlined style={{ fontSize: 92, width: 100 }} spin />;

class ContractInteraction extends React.Component {

    constructor() {
        super();
        this.state = {
            active: false,
            url: "",
            decryptionKey: "",
            nextTimeStamp: "",
            pendingTx: false,
            showIsActiveText: false,
            showTickText: false,
            showRenounceText: false,
            showGetDecryptionText: false,
            showDefuseText: false,
            showGetUrlText: false,
            showSetUrlText: false,
            showNextTimeStamp: false
        };
    }

    initContract = () => {
        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const contract = new ethers.Contract(this.props.address, DeadMansContract.abi, signer);
        return contract;
    }

    isActive = async () => {
        try {
            const tx = await this.initContract().isActive();
            this.setState({ pendingTx: false, showIsActiveText: true, active: tx });
        } catch (e) {
            console.log(e)
        }
    };

    tick = async () => {
        try {
            const tx = await this.initContract().tick();

            this.setState({ pendingTx: true });

            await tx.wait();
            this.setState({ pendingTx: false });
        } catch (e) {
            console.log(e);
        }
    };

    getNextTimeStamp = async () => {
        try {
            const tx = await this.initContract().getNextTimeStamp();
            this.setState({ pendingTx: false, showNextTimeStamp: true, nextTimeStamp: Date(parseInt(BigInt(tx))).toString() });
        } catch (e) {

        }
    }

    setUrl = async () => {
        try {
            const newUrl = document.getElementById("setUrl").value;
            const tx = await this.initContract().setUrl(newUrl);

            this.setState({ pendingTx: true });
            await tx.wait();
            this.setState({ pendingTx: false });
        } catch (e) {
            console.log(e);
        }
    };

    getUrl = async () => {
        try {
            const tx = await this.initContract().getUrl();
            this.setState({ pendingTx: true });

            await tx.wait();
            this.setState({ pendingTx: false, showGetUrlText: true, url: res });
        } catch (e) {
            console.log(e);
        }
    };

    getDecryptionKey = async () => {
        const tx = await this.initContract().getDecryptionKey();
        // Will error if  block timestamp < nextTimestamp ***
    };

    defuse = async () => {
        // Show warning modal
        const tx = await this.initContract().defuse();
    };

    renounceOwnershipModal = () => {
        this.state.isModalRenounceVisible = true;
    }

    renounceOwnership = async () => {
        // Pop warning modal before allowing this to happpen
        const tx = await this.initContract().renounceOwnership();
    };

  handleRenounceCancel = () => {
    this.state.isModalRenounceVisible = false;
  };

    render() {
        return (
            <div>
                {/* Give more emphasis to this */}
                <div>Contract Address: {this.props.address}</div>
                {this.state.pendingTx ? <Spin indicator={antIcon} style={{ width: "100%", marginTop: "4rem" }} /> : (
                    <Space direction="vertical">
                        <Button onClick={this.isActive}>Check Heartbeat</Button>
                        {this.state.showIsActiveText && <p>The switch is {this.state.active ? "still " : "not "} being monitored.</p>}
                        <Button onClick={this.tick}>Add Time</Button>
                        <Button onClick={this.getNextTimeStamp}>Get Next Time Stamp</Button>
                        {this.state.showNextTimeStamp && <p>{this.state.nextTimeStamp}</p>}
                        <Button onClick={this.defuse}>Defuse</Button>
                        <Button onClick={this.renounceOwnership}>Renounce Ownership</Button>
                        <Button onClick={this.getUrl}>Get URL</Button>
                        {this.state.showGetUrlText && <p>{this.state.url === "" ? "There was no URL set" : this.state.url}</p>}
                        <Button onClick={this.setUrl}>Set Url</Button>
                        <Input size="large" id="setUrl" />
                        <Button onClick={this.getDecryptionKey}>Reveal Decryption Key</Button>
                        {this.state.showGetDecryptionText && <p>{this.state.decryptionKey === "" ? "" : this.state.url}</p>}
                    </Space>
                )}
                <Modal id="renounce" visible={isModalRenounceVisible} onOk={handleOk} onCancel={handleCancel}><p>333</p></Modal>
                <Modal id="defuse" visible={isModalDefuseVisible} onOk={handleOkrenounceOwnership} onCancel={handleCancel}><p>123</p></Modal>
            </div>
        );
    };
}

export default ContractInteraction;
