import './App.css';
import DeadMansContract from './utils/DeadMansContract.abi.json';
import { ethers, ContractFactory } from "ethers";
import { useState } from 'react';

import { Layout, Collapse, Row, Col, Menu, Button, Input } from 'antd';
import "antd/dist/antd.css";
import { Typography, Divider, Spin } from 'antd';
import { LoadingOutlined } from '@ant-design/icons';

const { Title, Paragraph } = Typography;
const { Header, Content, Footer } = Layout;
const { Panel } = Collapse;
const antIcon = <LoadingOutlined style={{fontSize: 92, width: 100}} spin />;

function App() {
  const [currentAccount, setCurrentAccount] = useState("");
  const [isLoading, setIsLoading] = useState();

  const connectWallet = async () => {
    try {
      const { ethereum } = window;

      if (!ethereum) {
        alert("This app only works with MetaMask for now!");
        return;
      }

      const accounts = await ethereum.request({ method: "eth_requestAccounts" });
      console.log("Connected", accounts[0]);
      setCurrentAccount(accounts[0]);
    } catch (error) {
      console.log(error)
    }
  }

  const createContract = async () => {
    try {
      const { ethereum } = window;
      if (ethereum) {
        const provider = new ethers.providers.Web3Provider(ethereum)
        const signer = provider.getSigner();
        const decryptionKey = document.getElementById("decryptionKey").value;
        const cadence = document.getElementById("cadence").value;

        const factory = new ContractFactory(DeadMansContract.abi, DeadMansContract.bytecode, signer);

        const contract = await factory.deploy(decryptionKey, cadence);
        console.log("Transaction sent");
        setIsLoading(true);
        console.log(isLoading);
        //Show spiner and replace inputs with buttons to interact with smart contract

        await contract.deployTransaction.wait().then(() => {
          console.log("contract is deployed");
          console.log(contract.address);
          setIsLoading(false);
          //replace inputs with
        });

      } else {
        console.log("Ethereum object doesn't exist!")
      }
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <Layout>
      <Header style={{ position: "fixed", zIndex: 1, width: "100%" }}>
        <Menu theme="dark" mode="horizontal" >
          <Menu.Item key="1">nav 1</Menu.Item>
          <Menu.Item key="2">nav 2</Menu.Item>
          <Menu.Item key="3">nav 3</Menu.Item>
        </Menu>
      </Header>
      <Content style={{ marginTop: "8rem" }}>
        <Row justify="center">
          <Col span={18} id="inputs">
            <Title>Dead Man's Switch</Title>
            {!currentAccount ? (
              <button className="waveButton" onClick={connectWallet}>
                Connect Wallet
              </button>
            ) : <h1>Currently connected as: {currentAccount}</h1>
            }

            <Title level={2}>What is a Dead Man's Switch?</Title>
            <Paragraph>Lorem Ipsum. Some links. blah blah</Paragraph>
          <Spin indicator={antIcon} style={{alignSelf:'center'}} />
            {isLoading ? <Spin indicator={antIcon} style={{alignSelf:'center'}} /> : (
              <div>
                <Divider plain>Check on deployed Dead Man's Switch</Divider>
                <div>
                  <Input size="large" placeholder="Address of contract"></Input>
                  <Button style={{ marginTop: "2rem" }} onClick="getContract">Get Contract</Button>
                </div>

                <Divider plain>Deploy a new Dead Man's Switch</Divider>
                <div>
                  <Input size="large" id="decryptionKey" placeholder="Decryption Key"></Input>
                  <Input style={{ marginTop: "1.25rem" }} size="large" id="cadence" placeholder="When to check in hours" max="168"></Input>
                  <Button style={{ marginTop: "2rem" }} onClick={createContract}>Create Dead Man's Switch</Button>
                </div>
              </div>
            )}

            <div style={{ marginTop: "8rem" }}>
              <Collapse>
                <Panel header="Why is the blockchain perfect for this?"><p>Lorem Ipsum</p></Panel>
                <Panel header="How it works"><p>Lorem Ipsum</p></Panel>
                <Panel header="Does it cost anything?"><p>Lorem Ipsum</p></Panel>
              </Collapse>
            </div>
          </Col>
        </Row>

      </Content>

      <Footer>Disclaimer: This is not a legally binding thing nor should it be used for anything serious. Code can be found on: blah blah blah</Footer>
    </Layout>
  );
}

export default App;
